function CreateObject( amx, modelid, x, y, z, rx, ry, rz, isLowLOD )	local object = createObject( modelid, x, y, z, rx, ry, rz, isLowLOD );		if not object then		return false;	end		local objectID = addElem( g_Objects, object );		return objectID;endfunction MoveObject( amx, objectid, time, targetx, targety, targetz, 	moverx, movery, moverz, strEasingType, fEasingPeriod, fEasingAmplitude, fEasingOvershoot )		return moveObject( objectid, time, targetx, targety, targetz, 		moverx, movery, moverz, strEasingType, fEasingPeriod, fEasingAmplitude, fEasingOvershoot );endfunction StopObject( amx, objectid )	return stopObject( objectid );endfunction GetObjectScale( amx, objectid, refScale )	local scale = getObjectScale( objectid );	WriteMemFloat( amx, refScale, scale );endfunction SetObjectScale( amx, objectid, scale )	return setObjectScale( objectid, scale );end