function CreateObject( amx, modelid, x, y, z )	local object = createObject( modelid, x, y, z );		if not object then		return false;	end		local objectID = addElem( g_Objects, object );		return objectID;endfunction MoveObject( amx, objectid, time, targetx, targety, targetz )	return moveObject( objectid, time, targetx, targety, targetz );endfunction StopObject( amx, objectid )	return stopObject( objectid );endfunction GetObjectScale( amx, objectid, refScale )	local scale = getObjectScale( objectid );	WriteMemFloat( amx, refScale, scale );endfunction SetObjectScale( amx, objectid, scale )	return setObjectScale( objectid, scale );end