function CreatePickup( amx, x, y, z, type, ammount )	local pickup = createPickup( x, y, z, type, ammount );		if not pickup then		return false;	end		local pickupID = addElem( g_Pickups, pickup );		return pickupID;endfunction GetPickupAmmo( amx, pickup )	return getPickupAmmo( pickup );endfunction GetPickupAmount( amx, pickup )	return getPickupAmount( pickup );endfunction GetPickupType( amx, pickup, buf, bufSize )	local type = getPickupType( pickup );	if #type <= bufSize then		writeMemString( amx, buf, type );		return true;	end	return false;endfunction GetPickupWeapon( amx, pickup )	return getPickupWeapon( pickup );endfunction SetPickupType( amx, pickup, type )	return setPickupType( pickup, type );endfunction GetPickupRespawnInterval( amx, pickup )	return getPickupRespawnInterval( pickup );endfunction IsPickupSpawned( amx, pickup )	return isPickupSpawned( pickup );endfunction SetPickupRespawnInterval( amx, pickup, interval )	return setPickupRespawnInterval( pickup, interval );end function UsePickup( amx, pickup, player )	return usePickup( pickup, player );end